import { useNavigate, useParams } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import { useCookies } from 'react-cookie';
import classes from './Bookcover.module.css';
import axios from 'axios';

const BookCover = () => {
    const [Chapters, setChapters] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');
    const { bookName } = useParams();
    const navigate = useNavigate();
    const [cookie, setCookie] = useCookies('__book_id');

    const bookId = cookie.__book_id;

    useEffect(() => {
        const getChapters = async () => {
            try {
                isLoading(true);
                setError('');

                const response = await axios.get(
                    `https://app.prepanywhere.com/api/stu/static_books/all_chapters?id=${bookId}`
                );
                setChapters(response.data);
            } catch (error) {
                if (error.response) {
                    setError('No Data Found!');
                } else if (error.request) {
                    console.log(error.request);
                    setError('Network Error');
                } else {
                    setError('Something went wrong, Please try again!');
                }
            } finally {
                setIsLoading(false);
            }
        };

        const getBookDetails = async () => {
            try {
                axios
                    .get(
                        `https://app.prepanywhere.com/api/stu/static_books/book_details?id=82464444-790a-45dd-b74b-bff9d5cd622d`
                    )
                    .then((response) => {
                        console.log(response.data);
                    });
            } catch (err) {
                console.log('error while getting books', err);
            }
        };

        getChapters();
        getBookDetails();
    }, [bookId]);

    const navigationHandler = (url, id) => {
        navigate(url);
        setCookie('__material_id', id);
    };

    console.log(Chapters);

    return (
        <div className={classes.section}>
            <div className={classes.header}>
                <h1> {bookName.replace(/-/g, ' ')} </h1>
            </div>
            <div className={classes.container}>
                <div>
                    {Chapters.map((chapter) => {
                        return (
                            <div key={chapter.id}>
                                <div className={classes.chapterTitle}>
                                    <h2>{chapter.name}</h2>
                                </div>
                                <ul className={classes.chapterContainer}>
                                    <div className={classes.chapterTextContainer}>
                                        <li className={classes.listItem}>
                                            <div className={classes.materialWrapper}>
                                                <div className={classes.material}>
                                                    {chapter.materials.map((material) => {
                                                        return (
                                                            <div
                                                                key={material.id}
                                                                onClick={() =>
                                                                    navigationHandler(
                                                                        material.name.replace(
                                                                            / /g,
                                                                            '-'
                                                                        ),
                                                                        material.id
                                                                    )
                                                                }
                                                            >
                                                                <p>{material.name}</p>
                                                            </div>
                                                        );
                                                    })}
                                                </div>
                                            </div>
                                        </li>
                                    </div>
                                </ul>
                            </div>
                        );
                    })}
                </div>
            </div>
        </div>
    );
};

export default BookCover;
