import { useNavigate, useParams } from 'react-router-dom';
import React, { useState, useEffect, useContext } from 'react';
import { BookContext } from '../../context/BookContext';
import classes from './Bookcover.module.css';
import axios from 'axios';

const BookCover = () => {
    const [Chapters, setChapters] = useState([]);
    const { bookName } = useParams();
    const navigate = useNavigate();
    const { bookId } = useContext(BookContext);

    useEffect(() => {
        // Simulate loading data
        setTimeout(() => {
            // Find the product based on the product name (simulated lookup)
            const product = products.find((prod) => encodeURIComponent(prod.name) === bookName);
            if (product) {
                setProductId(product.id);
            }
            setLoading(false); // Set loading to false after a delay (simulating data retrieval)
        }, 2000); // Simulate a 2-second delay
    }, [bookName]);

    const getChapters = async () => {
        try {
            axios
                .get(`https://app.prepanywhere.com/api/stu/static_books/all_chapters?id=${bookId}`)
                .then((response) => {
                    setChapters(response.data);
                });
        } catch (err) {
            console.log('error while getting books', err);
        }
    };

    useEffect(() => {
        getChapters();
    }, []);

    return (
        <div className={classes.section}>
            <div className={classes.header}>
                <h1> {bookName.replace(/-/g, ' ')} </h1>
            </div>
            <div className={classes.container}>
                <div>
                    {Chapters.map((chapter) => {
                        return (
                            <div key={chapter.id}>
                                <div className={classes.chapterTitle}>
                                    <h2>{chapter.name}</h2>
                                </div>
                                <ul className={classes.chapterContainer}>
                                    <div className={classes.chapterTextContainer}>
                                        <li className={classes.listItem}>
                                            <div className={classes.materialWrapper}>
                                                <div className={classes.material}>
                                                    {chapter.materials.map((material) => {
                                                        return (
                                                            <div
                                                                key={material.id}
                                                                onClick={() => {
                                                                    navigate(
                                                                        material.name.replace(
                                                                            / /g,
                                                                            '-'
                                                                        )
                                                                    );
                                                                }}
                                                            >
                                                                <p>{material.name}</p>
                                                            </div>
                                                        );
                                                    })}
                                                </div>
                                            </div>
                                        </li>
                                    </div>
                                </ul>
                            </div>
                        );
                    })}
                </div>
            </div>
        </div>
    );
};

export default BookCover;
